from logger import logger
import pymongo
from pymongo import MongoClient

client = MongoClient("localhost", 27017)
# client.drop_database("pixeleconomy")  # TODO
db = client["pixeleconomy"]


def init():
    if db["db_meta"].find_one({"initialised": True}):
        logger.info("Database has already been initialised!")
        return

    user_bobjoss = db["users"].insert_one({
        "username": "bobJoss",
        "password": "password",
        "balance": 5000,
        "portfolio": [],
    }).inserted_id

    user_ilikemountains = db["users"].insert_one({
        "username": "ilikemountains",
        "password": "password",
        "balance": 3000,
        "portfolio": [],
    }).inserted_id

    user_bicasso = db["users"].insert_one({
        "username": "bicasso",
        "password": "password",
        "balance": 8000,
        "portfolio": [],
    }).inserted_id

    user_badartist = db["users"].insert_one({
        "username": "badartist",
        "password": "password",
        "balance": 3000,
        "portfolio": [],
    }).inserted_id

    art_higher_than = db["art"].insert_one({
        "title": "Higher than the Skies",
        "creator": user_ilikemountains,
        "data": "data:image/png;base64,"
    }).inserted_id

    art_grassy_hill = db["art"].insert_one({
        "title": "Grassy Hill",
        "creator": user_ilikemountains,
        "data": "data:image/png;base64,"
    }).inserted_id

    art_mt_abstract = db["art"].insert_one({
        "title": "Mt Abstract",
        "creator": user_ilikemountains,
        "data": "data:image/png;base64,",
    }).inserted_id

    db["users"].update_one({"_id": user_ilikemountains},
                           {"$push": {"portfolio": [art_higher_than, art_grassy_hill, art_mt_abstract]}})

    db["market"].insert_many([
        {
            "art": art_higher_than,
            "price": 130,
        },
        {
            "art": art_grassy_hill,
            "price": 100,
        },
        {
            "art": art_mt_abstract,
            "price": 42,
        }
    ])

    db["palettes"].insert_many([
        {
            "name": "Basic",
            "colours": [
                "#FFFFFF",
                "#000000"
            ],
            "price": 0,
        },
        {
            "name": "Greyscale",
            "colours": [
                "#DDDDDD",
                "#AAAAAA",
                "#777777",
                "#555555",
                "#222222"
            ],
            "price": 0
        },
        {
            "name": "Gameboy-like",
            "colours": [
                "#F9FFB3",
                "#ABCC47",
                "#3D8026",
                "#1B2E3A",
                "#00131A"
            ],
            "price": 0
        },
        {
            "name": "Monokai",
            "colours": [
                "#fd971f",
                "#a6e22e",
                "#66d9ef",
                "#f92672",
                "#272822"
            ],
            "price": 0
        }
    ])

    # update initialised status
    db["db_meta"].insert_one({
        "initialised": True,
    })
    logger.info("Initialised database!")


init()
